
package LinkedList1;
import java.util.LinkedList;

public class Linked {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		LinkedList ls=new LinkedList();
		ls.add(12);
		ls.add(121);
		ls.offer(1);
		System.out.println(ls);
		ls.offerFirst(1111);
		System.out.println(ls);
		ls.offerLast(10001);
		System.out.println(ls);
		

	}

}
